import{_ as e,c as t,o as a,d as r}from"./app.54004db6.js";const f=JSON.parse('{"title":"智力题 (3)","description":"","frontmatter":{},"headers":[{"level":2,"title":"老鼠喝毒酒","slug":"老鼠喝毒酒","link":"#老鼠喝毒酒","children":[]}],"relativePath":"basic/iq/3.md","lastUpdated":1679400365000}'),s={name:"basic/iq/3.md"},n=r('<h1 id="智力题-3" tabindex="-1">智力题 (3) <a class="header-anchor" href="#智力题-3" aria-hidden="true">#</a></h1><h2 id="老鼠喝毒酒" tabindex="-1">老鼠喝毒酒 <a class="header-anchor" href="#老鼠喝毒酒" aria-hidden="true">#</a></h2><p><strong>Q</strong>：15 瓶酒，里面有一瓶毒酒，老鼠喝完毒酒之后一天之内就会死去，至少需要几只小鼠才能在一天之内鉴别出毒酒？</p><p><strong>A</strong>：4 只。把 15 瓶酒按 1-15 编号，二进制表示最多 4 位，4 只老鼠分别喝每一位是 1 的酒，如果这一位的小鼠毒死了，说明毒酒编号的这一位是 1，组合所有 1 的位就能得到最终的编号。</p><p><strong>Q</strong>：那有四只小鼠，能鉴别多少瓶酒里的一瓶毒酒？</p><p><strong>A</strong>：16 瓶。如果 4 只老鼠都没毒死，说明毒酒是 0 号。</p>',6),o=[n];function _(i,c,d,p,h,l){return a(),t("div",null,o)}const m=e(s,[["render",_]]);export{f as __pageData,m as default};
