import{_ as t,c as e,o as l,d as i}from"./app.b2b4ff33.js";const P=JSON.parse('{"title":"协议栈","description":"","frontmatter":{},"headers":[{"level":2,"title":"OSI 七层协议","slug":"osi-七层协议","link":"#osi-七层协议","children":[]},{"level":2,"title":"TCP/IP 四层协议","slug":"tcp-ip-四层协议","link":"#tcp-ip-四层协议","children":[]},{"level":2,"title":"教学用的五层协议","slug":"教学用的五层协议","link":"#教学用的五层协议","children":[]}],"relativePath":"basic/network/stack.md","lastUpdated":1677593810000}'),a={name:"basic/network/stack.md"},r=i('<h1 id="协议栈" tabindex="-1">协议栈 <a class="header-anchor" href="#协议栈" aria-hidden="true">#</a></h1><h2 id="osi-七层协议" tabindex="-1">OSI 七层协议 <a class="header-anchor" href="#osi-七层协议" aria-hidden="true">#</a></h2><p>OSI (Open Systems Interconnection，开放系统互连) 协议栈包含 7 层：</p><ul><li>应用层 (Application)</li><li>表示层 (Presentation)</li><li>会话层 (Session)</li><li>传输层 (Transport)</li><li>网络层 (Network)</li><li>数据链路层 (Data Link)</li><li>物理层 (Physical)</li></ul><h2 id="tcp-ip-四层协议" tabindex="-1">TCP/IP 四层协议 <a class="header-anchor" href="#tcp-ip-四层协议" aria-hidden="true">#</a></h2><p>TCP/IP 是互联网协议栈的事实标准，包含 4 层：</p><ul><li>应用层 (Application) <ul><li>对应 OSI 的应用层、表示层、会话层</li></ul></li><li>传输层 (Transport)</li><li>网络层 (Network)</li><li>网际接口层 (Network Interface) <ul><li>对应 OSI 的数据链路层、物理层</li></ul></li></ul><h2 id="教学用的五层协议" tabindex="-1">教学用的五层协议 <a class="header-anchor" href="#教学用的五层协议" aria-hidden="true">#</a></h2><p>为了简化教学、突出重点，将计算机网络协议栈归纳为 5 层：</p><ul><li>应用层 <ul><li>对应 TCP/IP 的应用层</li></ul></li><li>运输层 <ul><li>对应传输层</li></ul></li><li>网络层</li><li>数据链路层</li><li>物理层</li></ul><table><thead><tr><th style="text-align:center;">层</th><th style="text-align:center;">协议</th></tr></thead><tbody><tr><td style="text-align:center;">应用层</td><td style="text-align:center;">HTTP、DNS、SSH、FTP、SFTP、SMTP、POP3、IMAP</td></tr><tr><td style="text-align:center;">运输层</td><td style="text-align:center;">TCP、UDP</td></tr><tr><td style="text-align:center;">网络层</td><td style="text-align:center;">IP</td></tr><tr><td style="text-align:center;">数据链路层</td><td style="text-align:center;">P2P、CSMA/CD</td></tr><tr><td style="text-align:center;">物理层</td><td style="text-align:center;"></td></tr></tbody></table>',11),n=[r];function d(s,c,o,h,p,_){return l(),e("div",null,n)}const x=t(a,[["render",d]]);export{P as __pageData,x as default};
