import{_ as e,c as i,o as t,a as n}from"./app.048909d3.js";const m=JSON.parse('{"title":"CSS \u52A8\u753B","description":"","frontmatter":{},"headers":[{"level":2,"title":"transition","slug":"transition"},{"level":3,"title":"transition-property","slug":"transition-property"},{"level":3,"title":"transition-duration","slug":"transition-duration"},{"level":3,"title":"transition-timing-function","slug":"transition-timing-function"},{"level":3,"title":"transition-delay","slug":"transition-delay"},{"level":3,"title":"transitionend \u4E8B\u4EF6","slug":"transitionend-\u4E8B\u4EF6"}],"relativePath":"css/animation.md"}'),a={name:"css/animation.md"},o=n('<h1 id="css-\u52A8\u753B" tabindex="-1">CSS \u52A8\u753B <a class="header-anchor" href="#css-\u52A8\u753B" aria-hidden="true">#</a></h1><p>\u6709\u4E24\u79CD CSS \u52A8\u753B\uFF1A</p><ul><li><code>transition</code></li><li><code>@keyframes</code></li></ul><h2 id="transition" tabindex="-1">transition <a class="header-anchor" href="#transition" aria-hidden="true">#</a></h2><p>\u53EA\u9700\u8981\u5B9A\u4E49\u4EE5\u4E0B 4 \u4E2A\u5C5E\u6027\uFF0C\u5373\u53EF\u5B9E\u73B0\u5C5E\u6027\u503C\u6539\u53D8\u65F6\u81EA\u52A8\u5E94\u7528\u52A8\u753B\uFF1A</p><ul><li><code>transition-property</code></li><li><code>transition-duration</code></li><li><code>transition-timing-function</code></li><li><code>transition-delay</code></li></ul><p>\u7B80\u5199\u5C5E\u6027\uFF1A<code>transition: property duration timing-function delay</code></p><h3 id="transition-property" tabindex="-1">transition-property <a class="header-anchor" href="#transition-property" aria-hidden="true">#</a></h3><p>\u5927\u90E8\u5206 CSS \u5C5E\u6027\u53EF\u4EE5\u5E94\u7528\u52A8\u753B\uFF0C\u4F46\u4E0D\u662F\u5168\u90E8\u3002<code>all</code> \u8868\u793A\u5E94\u7528\u4E8E\u6240\u6709\u5C5E\u6027\u3002</p><h3 id="transition-duration" tabindex="-1">transition-duration <a class="header-anchor" href="#transition-duration" aria-hidden="true">#</a></h3><p>\u52A8\u753B\u6301\u7EED\u7684\u65F6\u95F4\uFF0C\u5355\u4F4D\u4E3A s \u6216 ms\u3002</p><h3 id="transition-timing-function" tabindex="-1">transition-timing-function <a class="header-anchor" href="#transition-timing-function" aria-hidden="true">#</a></h3><p>\u63CF\u8FF0\u52A8\u753B\u8FDB\u7A0B\u5728\u65F6\u95F4\u4E0A\u7684\u5206\u5E03\uFF0C\u6BD4\u5982\u5148\u6162\u540E\u5FEB\u8FD8\u662F\u5148\u5FEB\u540E\u6162\u3002\u63A5\u53D7 2 \u79CD\u503C\uFF1A\u4E09\u9636\u8D1D\u585E\u5C14\u66F2\u7EBF\u3001\u9636\u8DC3\u51FD\u6570\u3002</p><h4 id="\u4E09\u9636\u8D1D\u585E\u5C14\u66F2\u7EBF-cubic-bezier-curve" tabindex="-1">\u4E09\u9636\u8D1D\u585E\u5C14\u66F2\u7EBF (Cubic bezier curve) <a class="header-anchor" href="#\u4E09\u9636\u8D1D\u585E\u5C14\u66F2\u7EBF-cubic-bezier-curve" aria-hidden="true">#</a></h4><p>\u8D1D\u585E\u5C14\u66F2\u7EBF\u7684\u9636\u6B21\u7B49\u4E8E\u63A7\u5236\u70B9\u7684\u4E2A\u6570\u51CF\u4E00\uFF0C\u56E0\u6B64\u4E09\u9636\u8D1D\u585E\u5C14\u66F2\u7EBF\u9700\u8981 4 \u4E2A\u63A7\u5236\u70B9\u3002</p><p>\u7B2C 1 \u4E2A\u70B9\u56FA\u5B9A\u4E3A <code>(0, 0)</code>\uFF0C\u7B2C 4 \u4E2A\u70B9\u56FA\u5B9A\u4E3A <code>(1, 1)</code>\uFF0C\u53EA\u9700\u8981\u6307\u5B9A\u4E2D\u95F4\u7684\u4E24\u4E2A\u70B9\u3002</p><p><code>transition-timing-function: cubic-bezier(x2, y2, x3, y3)</code></p><p>x \u8868\u793A\u65F6\u95F4\uFF0Cy \u8868\u793A\u8FC7\u7A0B\u7684\u5B8C\u6210\u5EA6\u3002</p><p>CSS \u63D0\u4F9B\u51E0\u6761\u5185\u5EFA\u7684\u66F2\u7EBF\uFF1A</p><ul><li><code>ease</code> (\u9ED8\u8BA4\u503C)\uFF1A<code>(0.25, 0.1, 0.25, 1.0)</code>\uFF0C\u7A0D\u6162=&gt;\u5FEB=&gt;\u6162</li><li><code>ease-in</code>\uFF1A<code>(0.42, 0, 1.0, 1.0)</code>\uFF0C\u6162=&gt;\u5FEB</li><li><code>ease-out</code>\uFF1A<code>(0, 0, 0.58, 1.0)</code>\uFF0C\u5FEB=&gt;\u6162</li><li><code>ease-in-out</code>\uFF1A<code>(0.42, 0, 0.58, 1.0)</code>\uFF0C\u6162=&gt;\u5FEB=&gt;\u6162</li></ul><p><img src="https://s2.loli.net/2022/07/19/4SJNUPiTwGzAY5K.png" alt="\u56FE\u7247"></p><p>x \u5FC5\u987B\u5728 <code>[0, 1]</code> \u4E4B\u95F4\uFF0Cy \u7684\u53D6\u503C\u6CA1\u6709\u9650\u5236\uFF0C\u56E0\u6B64\u53EF\u4EE5\u4F7F\u52A8\u753B\u8D85\u51FA\u8D77\u70B9\u548C\u7EC8\u70B9\u4E4B\u95F4\u7684\u8303\u56F4\u3002</p><h4 id="\u9636\u8DC3\u51FD\u6570-steps-function" tabindex="-1">\u9636\u8DC3\u51FD\u6570 (Steps function) <a class="header-anchor" href="#\u9636\u8DC3\u51FD\u6570-steps-function" aria-hidden="true">#</a></h4><p>\u628A\u52A8\u753B\u8FC7\u7A0B\u5206\u6210\u82E5\u5E72\u4E2A\u7B49\u957F\u7684\u95F4\u9694\uFF0C\u8FDB\u884C\u79BB\u6563\u7684\u53D8\u5316\u3002</p><p><code>transition-timing-function: steps(number of steps[, start/end])</code></p><p><code>start</code> \u8868\u793A\u53D8\u5316\u53D1\u751F\u5728\u6BCF\u4E00\u6BB5\u7684\u5F00\u59CB\uFF0C<code>end</code> \u8868\u793A\u53D8\u5316\u53D1\u751F\u5728\u6BCF\u4E00\u6BB5\u7684\u7ED3\u5C3E\u3002</p><p>\u7B80\u5199\u503C\uFF1A</p><ul><li><code>step-start</code>\uFF1A<code>steps(1, start)</code>\uFF0C\u53EA\u6709 1 \u6BB5\u4E14\u7ACB\u5373\u5F00\u59CB\uFF0C\u76F8\u5F53\u4E8E\u6CA1\u6709\u52A8\u753B\u3002</li><li><code>step-end</code>\uFF1A<code>steps(1, end)</code>\uFF0C\u53EA\u6709 1 \u6BB5\uFF0C<code>duration</code> \u65F6\u957F\u540E\u53D1\u751F\u53D8\u5316\uFF0C\u76F8\u5F53\u4E8E\u53EA\u6709\u5EF6\u65F6\u6548\u679C\u3002</li></ul><h3 id="transition-delay" tabindex="-1">transition-delay <a class="header-anchor" href="#transition-delay" aria-hidden="true">#</a></h3><p>\u52A8\u753B\u5F00\u59CB\u524D\u7684\u5EF6\u8FDF\u65F6\u95F4\uFF0C\u5355\u4F4D\u4E3A s \u6216 ms\u3002\u8BBE\u4E3A\u8D1F\u6570\u8868\u793A\u8DF3\u8FC7\u4E00\u6BB5\u65F6\u95F4\u7684\u52A8\u753B\uFF0C\u4ECE\u4E2D\u95F4\u5F00\u59CB\u64AD\u653E\u52A8\u753B\u3002</p><h3 id="transitionend-\u4E8B\u4EF6" tabindex="-1">transitionend \u4E8B\u4EF6 <a class="header-anchor" href="#transitionend-\u4E8B\u4EF6" aria-hidden="true">#</a></h3><p>\u52A8\u753B\u5B8C\u6210\u540E\uFF0C\u4F1A\u89E6\u53D1 <code>transitionend</code> \u4E8B\u4EF6\u3002</p><p>\u4E8B\u4EF6\u5BF9\u8C61\u6709\u51E0\u4E2A\u7279\u5B9A\u5C5E\u6027\uFF1A</p><ul><li><code>propertyName</code>\uFF1A\u5B8C\u6210\u52A8\u753B\u7684\u5C5E\u6027\u540D\u3002</li><li><code>elapsedTime</code>\uFF1A\u52A8\u753B\u5B8C\u6210\u7684\u65F6\u95F4 (s)\uFF0C\u4E0D\u5305\u62EC <code>delay</code>\u3002</li></ul>',34),d=[o];function r(c,s,l,p,h,u){return t(),i("div",null,d)}var _=e(a,[["render",r]]);export{m as __pageData,_ as default};
