import{_ as i,c as l,o as e,a as o}from"./app.b1f5e497.js";const _=JSON.parse('{"title":"BFC/IFC","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5757\u7EA7/\u884C\u7EA7\u76D2\u5B50","slug":"\u5757\u7EA7-\u884C\u7EA7\u76D2\u5B50"},{"level":2,"title":"BFC","slug":"bfc"},{"level":2,"title":"IFC","slug":"ifc"}],"relativePath":"css/bfc-ifc.md"}'),c={name:"css/bfc-ifc.md"},a=o('<h1 id="bfc-ifc" tabindex="-1">BFC/IFC <a class="header-anchor" href="#bfc-ifc" aria-hidden="true">#</a></h1><p>CSS \u6709 3 \u79CD\u5B9A\u4F4D\u65B9\u5F0F\uFF1A</p><ul><li>\u5E38\u89C4\u6D41 (Normal flow) <ul><li>BFC (Block Formatting Context\uFF0C\u5757\u7EA7\u6392\u7248\u4E0A\u4E0B\u6587)</li><li>IFC (Inline Formatting Context\uFF0C\u884C\u7EA7\u6392\u7248\u4E0A\u4E0B\u6587)</li><li>\u76F8\u5BF9\u5B9A\u4F4D (Relative positioning)</li><li>table \u6392\u7248\u4E0A\u4E0B\u6587</li><li>flex \u6392\u7248\u4E0A\u4E0B\u6587</li><li>grid \u6392\u7248\u4E0A\u4E0B\u6587</li></ul></li><li>\u6D6E\u52A8 (Floats)</li><li>\u7EDD\u5BF9\u5B9A\u4F4D (Absolute positioning) <ul><li><code>absolute</code></li><li><code>fixed</code></li></ul></li></ul><h2 id="\u5757\u7EA7-\u884C\u7EA7\u76D2\u5B50" tabindex="-1">\u5757\u7EA7/\u884C\u7EA7\u76D2\u5B50 <a class="header-anchor" href="#\u5757\u7EA7-\u884C\u7EA7\u76D2\u5B50" aria-hidden="true">#</a></h2><p><code>display</code> \u4E0E BFC/IFC \u76F8\u5173\u7684\u53D6\u503C\uFF1A</p><ul><li><code>block</code>\uFF1A\u751F\u6210\u5757\u7EA7\u76D2\u5B50 (block-level box)\uFF0C\u4E00\u4E2A\u76D2\u5B50\u5360\u4E00\u884C</li><li><code>inline</code>\uFF1A\u751F\u6210\u884C\u7EA7\u76D2\u5B50 (inline-level box)\uFF0C\u4E0E\u5176\u4ED6\u884C\u7EA7\u76D2\u5B50\u6446\u653E\u5728\u4E00\u884C <ul><li>\u4E0D\u80FD\u8BBE\u7F6E <code>width</code> \u548C <code>height</code></li></ul></li><li><code>inline-block</code>\uFF1A\u751F\u6210\u884C\u7EA7\u76D2\u5B50\uFF0C\u53EF\u4EE5\u8BBE\u7F6E\u5BBD\u9AD8\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u6574\u4F53\u4E0D\u4F1A\u88AB\u62C6\u6210\u591A\u884C</li><li><code>none</code>\uFF1A\u6392\u7248\u65F6\u88AB\u5FFD\u7565</li></ul><h2 id="bfc" tabindex="-1">BFC <a class="header-anchor" href="#bfc" aria-hidden="true">#</a></h2><p>\u4EE5\u4E0B\u5143\u7D20\u4F1A\u521B\u5EFA\u4E00\u4E2A BFC\uFF1A</p><ul><li>\u6839\u5143\u7D20</li><li><code>inline-block</code>\u3001\u6D6E\u52A8\u3001\u7EDD\u5BF9\u5B9A\u4F4D</li><li>flex \u5B50\u9879\u548C grid \u5B50\u9879</li><li><code>overflow</code> \u4E0D\u662F <code>visible</code> \u7684\u5757\u7EA7\u76D2\u5B50</li><li><code>display: flow-root;</code></li></ul><p>\u6392\u7248\u89C4\u5219\uFF1A</p><ul><li>\u76D2\u5B50\u4ECE\u4E0A\u5230\u4E0B\u6392\u5217</li><li>\u5782\u76F4 margin \u5408\u5E76</li><li>BFC \u5185 margin \u4E0D\u4F1A\u4E0E\u5916\u754C margin \u5408\u5E76</li><li>\u4E0D\u4F1A\u4E0E\u6D6E\u52A8\u5143\u7D20\u91CD\u53E0</li></ul><h2 id="ifc" tabindex="-1">IFC <a class="header-anchor" href="#ifc" aria-hidden="true">#</a></h2><p>\u53EA\u5305\u542B\u884C\u7EA7\u76D2\u5B50\u7684\u5BB9\u5668\u5143\u7D20\u4F1A\u521B\u5EFA\u4E00\u4E2A IFC\u3002</p><p>\u6392\u7248\u89C4\u5219\uFF1A</p><ul><li>\u76D2\u5B50\u5728\u4E00\u884C\u5185\u6C34\u5E73\u6392\u5217\uFF0C\u4E00\u884C\u653E\u4E0D\u4E0B\u65F6\u6362\u884C\u663E\u793A</li><li><code>text-align</code> \u51B3\u5B9A\u4E00\u884C\u5185\u76D2\u5B50\u7684\u6C34\u5E73\u5BF9\u9F50</li><li><code>vertical-align</code> \u51B3\u5B9A\u4E00\u4E2A\u76D2\u5B50\u5728\u884C\u5185\u7684\u5782\u76F4\u5BF9\u9F50</li><li>\u73AF\u7ED5\u6D6E\u52A8\u5143\u7D20</li></ul>',15),d=[a];function t(n,r,s,f,h,u){return e(),l("div",null,d)}var b=i(c,[["render",t]]);export{_ as __pageData,b as default};
