import{_ as i,c as l,o as e,a as t}from"./app.87442856.js";var a="/assets/vitals.d2fcefc5.png",o="/assets/lcp-demo-1.cfa09495.avif",s="/assets/lcp-demo-2.3595a1ae.avif",d="/assets/lcp-demo-3.e6163528.avif",r="/assets/fid-demo-1.8464335d.svg",c="/assets/session-window.d5700f86.webm",n="/assets/cls-demo.ee3690b9.avif";const P=JSON.parse('{"title":"Web \u6027\u80FD\u6307\u6807","description":"","frontmatter":{},"headers":[{"level":2,"title":"LCP","slug":"lcp"},{"level":2,"title":"FID","slug":"fid"},{"level":2,"title":"CLS","slug":"cls"},{"level":2,"title":"\u6D4B\u91CF\u5DE5\u5177","slug":"\u6D4B\u91CF\u5DE5\u5177"}],"relativePath":"design/web-vitals.md"}'),p={name:"design/web-vitals.md"},u=t('<h1 id="web-\u6027\u80FD\u6307\u6807" tabindex="-1">Web \u6027\u80FD\u6307\u6807 <a class="header-anchor" href="#web-\u6027\u80FD\u6307\u6807" aria-hidden="true">#</a></h1><ul><li>LCP (Largest Contentful Paint) <ul><li>\u6700\u5927\u5185\u5BB9\u7ED8\u5236</li><li>\u6D4B\u91CF\u52A0\u8F7D\u6027\u80FD (Loading)</li><li>\u5E94\u8BE5\u5C0F\u4E8E 2.5 s</li></ul></li><li>FID (First Input Delay) <ul><li>\u9996\u6B21\u8F93\u5165\u5EF6\u8FDF</li><li>\u6D4B\u91CF\u4EA4\u4E92\u6027 (Interactivity)</li><li>\u5E94\u8BE5\u5C0F\u4E8E 100 ms</li></ul></li><li>CLS (Cumulative Layout Shift) <ul><li>\u7D2F\u79EF\u5E03\u5C40\u504F\u79FB</li><li>\u6D4B\u91CF\u89C6\u89C9\u7A33\u5B9A\u6027 (Visual Stability)</li><li>\u5E94\u8BE5\u5C0F\u4E8E 0.1</li></ul></li></ul><p><img src="'+a+'" alt=""></p><h2 id="lcp" tabindex="-1">LCP <a class="header-anchor" href="#lcp" aria-hidden="true">#</a></h2><p>\u9875\u9762\u53EF\u89C6\u533A\u57DF\u5185\u6700\u5927\u5143\u7D20\u5B8C\u6210\u6E32\u67D3\u7684\u65F6\u95F4\uFF0C\u76F8\u5BF9\u4E8E\u9875\u9762\u9996\u6B21\u5F00\u59CB\u52A0\u8F7D\u7684\u65F6\u95F4\u3002</p><p>\u8003\u8651\u7684\u5143\u7D20\u7C7B\u578B\uFF1A</p><ul><li><code>&lt;img&gt;</code> \u5143\u7D20</li><li>\u5185\u5D4C\u5728 <code>&lt;svg&gt;</code> \u5143\u7D20\u4E2D\u7684 <code>&lt;image&gt;</code> \u5143\u7D20</li><li><code>&lt;video&gt;</code> \u5143\u7D20</li><li>\u901A\u8FC7 <code>url()</code> \u51FD\u6570\u52A0\u8F7D\u80CC\u666F\u56FE\u7247\u7684\u5143\u7D20</li><li>\u5305\u542B\u6587\u672C\u8282\u70B9\u6216\u884C\u7EA7\u6587\u672C\u5143\u7D20\u7684\u5757\u7EA7\u5143\u7D20</li></ul><p>\u786E\u5B9A\u5143\u7D20\u7684\u5927\u5C0F\uFF1A</p><ul><li>\u53EA\u8003\u8651\u53EF\u89C1\u533A\u57DF\uFF0C\u4E0D\u8003\u8651\u6EA2\u51FA\u90E8\u5206</li><li>\u5BF9\u4E8E\u56FE\u7247\uFF0C\u8003\u8651\u539F\u59CB\u5C3A\u5BF8\u548C\u53EF\u89C1\u5C3A\u5BF8\u7684\u8F83\u5C0F\u503C</li><li>\u5BF9\u4E8E\u6587\u672C\uFF0C\u53EA\u8003\u8651\u6587\u672C\u8282\u70B9\u7684\u5927\u5C0F (\u5305\u542B\u6240\u6709\u6587\u672C\u8282\u70B9\u7684\u6700\u5C0F\u77E9\u5F62)</li><li>\u4E0D\u8003\u8651 margin\u3001border\u3001padding</li></ul><p>\u8BA1\u7B97\u8FC7\u7A0B\uFF1A</p><ul><li>\u6D4F\u89C8\u5668\u7ED8\u5236\u7B2C\u4E00\u5E27\u540E\u7ACB\u5373\u5206\u53D1\u4E00\u4E2A LCP \u7C7B\u578B\u7684 <code>PerformanceEntry</code></li><li>\u6E32\u67D3\u540E\u7EED\u5E27\u65F6\uFF0C\u5982\u679C\u6709\u65B0\u7684<strong>\u53EF\u89C1</strong>\u5143\u7D20\u6BD4\u6700\u5927\u5143\u7D20\u66F4\u5927\uFF0C\u5206\u53D1\u4E00\u4E2A\u65B0\u7684 <code>PerformanceEntry</code><ul><li>\u53EA\u4F1A\u8003\u8651\u53EF\u89C1\u5143\u7D20\u521D\u6B21\u6E32\u67D3\u65F6\u7684\u5927\u5C0F\uFF0C\u540E\u7EED\u7684\u5C3A\u5BF8\u548C\u4F4D\u7F6E\u6539\u53D8\u4E0D\u4F1A\u88AB\u8003\u8651</li><li>\u5982\u679C\u5143\u7D20\u5728\u5C4F\u5E55\u5916\u5B8C\u6210\u6E32\u67D3\uFF0C\u540E\u7EED\u53D8\u4E3A\u53EF\u89C1\uFF0C\u4E0D\u4F1A\u88AB\u8003\u8651</li></ul></li><li>\u5982\u679C\u6700\u5927\u5143\u7D20\u88AB\u79FB\u9664\uFF0C\u4ECD\u4FDD\u6301\u8FD9\u4E2A\u5143\u7D20\u4E3A\u6700\u5927\u5143\u7D20</li><li>\u5F53\u7528\u6237\u5F00\u59CB\u4EA4\u4E92\u65F6 (\u70B9\u51FB\u3001\u6EDA\u52A8\u3001\u89E6\u6478)\uFF0C\u6D4F\u89C8\u5668\u505C\u6B62\u5206\u53D1 <code>PerformanceEntry</code></li><li>\u6700\u65B0\u7684\u4E00\u6761 <code>PerformanceEntry</code> \u4EE3\u8868\u4E86\u7ED8\u5236\u7684\u6700\u5927\u5185\u5BB9</li></ul><p><img src="'+o+'" alt=""><img src="'+s+'" alt=""><img src="'+d+'" alt=""></p><h2 id="fid" tabindex="-1">FID <a class="header-anchor" href="#fid" aria-hidden="true">#</a></h2><p>\u4ECE\u7528\u6237\u7B2C\u4E00\u6B21\u4E0E\u9875\u9762\u4EA4\u4E92\u5230\u6D4F\u89C8\u5668\u505A\u51FA\u54CD\u5E94\u3001\u5F00\u59CB\u6267\u884C\u5904\u7406\u7A0B\u5E8F\u6240\u9700\u7684\u65F6\u95F4\u3002</p><ul><li>\u53EA\u8003\u8651\u4E0D\u8FDE\u7EED\u64CD\u4F5C (\u70B9\u51FB\u3001\u89E6\u6478)\uFF0C\u4E0D\u8003\u8651\u8FDE\u7EED\u64CD\u4F5C (\u6EDA\u52A8\u3001\u7F29\u653E)</li><li>\u6D4B\u91CF\u4ECE\u63A5\u6536\u5230\u4E8B\u4EF6\u5230\u4E3B\u7EBF\u7A0B\u4E0B\u4E00\u6B21\u7A7A\u95F2\u7684\u65F6\u95F4\u5DEE\u503C\uFF0C\u6CA1\u6709\u5904\u7406\u7A0B\u5E8F\u4E5F\u53EF\u4EE5\u8BA1\u7B97 FID</li><li>\u8F93\u5165\u5EF6\u8FDF\u4E3B\u8981\u662F\u7531\u9875\u9762\u52A0\u8F7D\u8FC7\u7A0B\u4E2D\u957F\u4EFB\u52A1\u7684\u963B\u585E\u9020\u6210\u7684</li></ul><p><img src="'+r+'" alt=""></p><h2 id="cls" tabindex="-1">CLS <a class="header-anchor" href="#cls" aria-hidden="true">#</a></h2><p>\u4F1A\u8BDD\u7A97\u53E3\u5185\u6240\u6709\u610F\u5916\u5E03\u5C40\u504F\u79FB\u7684\u6700\u5927\u7D2F\u8BA1\u5206\u6570\u3002</p><p>\u4F1A\u8BDD\u7A97\u53E3 (session window)\uFF1A\u8FDE\u7EED\u53D1\u751F\u7684\u4E00\u8FDE\u4E32\u5E03\u5C40\u504F\u79FB\uFF0C\u6BCF\u6B21\u504F\u79FB\u7684\u95F4\u9694\u65F6\u95F4\u5C0F\u4E8E 1 \u79D2\uFF0C\u7A97\u53E3\u7684\u6700\u5927\u65F6\u957F\u4E3A 5 \u79D2\u3002</p><p><video src="'+c+'" muted controls width="70%"></video></p><p>\u5E03\u5C40\u504F\u79FB (layout shift)\uFF1A\u53EF\u89C1\u5143\u7D20\u7684\u8D77\u59CB\u4F4D\u7F6E\u5728\u76F8\u90BB\u4E24\u5E27\u4E4B\u95F4\u53D1\u751F\u4E86\u53D8\u66F4\u3002</p><ul><li>\u4E0D\u8003\u8651\u65B0\u5143\u7D20\u6DFB\u52A0\u5230 DOM \u6216\u5DF2\u6709\u5143\u7D20\u7684\u5927\u5C0F\u53D1\u751F\u6539\u53D8</li><li>\u5982\u679C\u67D0\u4E2A\u5143\u7D20\u7684\u5927\u5C0F\u6539\u53D8\u9020\u6210\u5176\u4ED6\u5143\u7D20\u7684\u8D77\u59CB\u4F4D\u7F6E\u53D8\u66F4\uFF0C\u89C6\u4E3A\u5176\u4ED6\u5143\u7D20\u7684\u5E03\u5C40\u504F\u79FB</li><li>\u6D4F\u89C8\u5668\u4F1A\u5206\u53D1 <code>layout-shift</code> \u7C7B\u578B\u7684 <code>PerformanceEntry</code></li></ul><p>\u4E0D\u7A33\u5B9A\u5143\u7D20\uFF1A\u53D1\u751F\u5E03\u5C40\u504F\u79FB\u7684\u5143\u7D20\u3002</p><p>\u5E03\u5C40\u504F\u79FB\u5206\u6570 = \u5F71\u54CD\u5206\u6570 (impact fraction) * \u8DDD\u79BB\u5206\u6570 (distance fraction)</p><p>\u5F71\u54CD\u5206\u6570\uFF1A\u76F8\u90BB\u4E24\u5E27<strong>\u6240\u6709</strong>\u4E0D\u7A33\u5B9A\u5143\u7D20\u7684\u53EF\u89C1\u533A\u57DF\u96C6\u5408\u5360\u603B\u53EF\u89C1\u533A\u57DF\u7684\u6BD4\u4F8B\u3002</p><p>\u8DDD\u79BB\u5206\u6570\uFF1A\u4E0D\u7A33\u5B9A\u5143\u7D20\u5728\u76F8\u90BB\u4E24\u5E27\u4E2D\u4F4D\u79FB\u7684<strong>\u6700\u5927</strong>\u8DDD\u79BB\u5360\u53EF\u89C1\u533A\u57DF\u8F83\u5927\u5C3A\u5BF8\u7EF4\u5EA6\u7684\u6BD4\u4F8B (\u5BBD\u5EA6\u6216\u9AD8\u5EA6\uFF0C\u4EE5\u8F83\u5927\u8005\u4E3A\u51C6)\u3002</p><img src="'+n+'" width="70%"><p>\u9884\u671F\u5E03\u5C40\u504F\u79FB\uFF1A\u5BF9\u7528\u6237\u4EA4\u4E92\u8FDB\u884C\u54CD\u5E94\u7684\u5E03\u5C40\u504F\u79FB\u901A\u5E38\u6CA1\u6709\u95EE\u9898\uFF0C\u5728\u7528\u6237\u8FDB\u884C\u4E0D\u8FDE\u7EED\u8F93\u5165 500 ms \u5185\u7684\u5E03\u5C40\u504F\u79FB\u4F1A\u5E26\u6709 <code>hadRecentInput</code> \u6807\u8BB0\uFF0CCLS \u7684\u8BA1\u7B97\u4F1A\u6392\u9664\u8FD9\u4E9B\u504F\u79FB\u3002</p><ul><li>CSS \u7684 <code>transform</code> \u4E0D\u6539\u53D8\u5143\u7D20\u7684\u8D77\u59CB\u4F4D\u7F6E\uFF0C\u4E0D\u4F1A\u89E6\u53D1\u5E03\u5C40\u504F\u79FB <ul><li>\u7528 <code>scale()</code> \u4EE3\u66FF\u5BF9 <code>width</code> \u548C <code>height</code> \u7684\u4FEE\u6539</li><li>\u7528 <code>translate()</code> \u4EE3\u66FF\u5BF9 <code>top/bottom/left/right</code> \u7684\u4FEE\u6539</li></ul></li></ul><p>\u610F\u5916\u5E03\u5C40\u504F\u79FB\uFF1A\u4E0D\u5C5E\u4E8E\u9884\u671F\u5E03\u5C40\u504F\u79FB\u5373\u4E3A\u610F\u5916\u5E03\u5C40\u504F\u79FB\u3002</p><h2 id="\u6D4B\u91CF\u5DE5\u5177" tabindex="-1">\u6D4B\u91CF\u5DE5\u5177 <a class="header-anchor" href="#\u6D4B\u91CF\u5DE5\u5177" aria-hidden="true">#</a></h2><ul><li>web-vitals \u5E93</li><li>Chrome DevTools Lighthouse</li></ul>',32),h=[u];function m(_,f,g,v,b,w){return e(),l("div",null,h)}var S=i(p,[["render",m]]);export{P as __pageData,S as default};
