import{_ as a,c as s,o as i,d as e}from"./app.71c38c5d.js";const l="/assets/cloud-service.1309eef2.png",t="/assets/iaas-paas-saas.6be4ce65.jpeg",r="/assets/azure-iaas-paas-saas.60347c30.jpeg",g=JSON.parse('{"title":"IaaS/PaaS/SaaS","description":"","frontmatter":{},"headers":[],"relativePath":"fe/optimization/design/iaas-paas-saas.md","lastUpdated":1680318810000}'),o={name:"fe/optimization/design/iaas-paas-saas.md"},c=e('<h1 id="iaas-paas-saas" tabindex="-1">IaaS/PaaS/SaaS <a class="header-anchor" href="#iaas-paas-saas" aria-hidden="true">#</a></h1><ul><li>IaaS (Infrastructure as a Service)：基础设施服务 <ul><li>计算、存储、网络资源</li><li>AWS、阿里云、腾讯云</li></ul></li><li>PaaS (Platform as a Service)：平台服务 <ul><li>操作系统、数据库、消息队列、执行环境、安全</li><li>CentOS、MySQL、RabbitMQ、Node</li></ul></li><li>SaaS (Software as a Service)：软件服务 <ul><li>邮件、IM、社交软件等具体的业务应用</li><li>几乎每个应用都可以是 SaaS</li></ul></li></ul><p><img src="'+l+'" alt=""></p><p>比如你想开一家披萨店 (你的产品)，但是从头到尾都自己准备太麻烦了，你打算外包一部分工作，采用他人的服务 (云服务)：</p><ul><li>IaaS <ul><li>他人提供厨房、炉子、煤气，并负责出问题时及时修复</li></ul></li><li>PaaS <ul><li>他人还提供披萨饼皮，你只需要设计配料然后烤出来</li></ul></li><li>SaaS <ul><li>他人提供一个做好的披萨，你只需要重新包装一下然后卖出去</li></ul></li></ul><p><img src="'+t+'" alt=""></p><p><img src="'+r+'" alt=""></p>',7),p=[c];function _(n,S,u,d,m,f){return i(),s("div",null,p)}const P=a(o,[["render",_]]);export{g as __pageData,P as default};
