import{_ as l,c as i,o as e,a as t}from"./app.18330815.js";const L=JSON.parse('{"title":"HTTPS","description":"","frontmatter":{},"headers":[],"relativePath":"http/https.md"}'),p={name:"http/https.md"},s=t('<h1 id="https" tabindex="-1">HTTPS <a class="header-anchor" href="#https" aria-hidden="true">#</a></h1><p>HTTP \u662F\u4E0D\u52A0\u5BC6\u7684\u660E\u6587\u4F20\u8F93\uFF0C\u5E26\u6765\u4E86\u4E09\u5927\u98CE\u9669\uFF1A</p><ul><li>\u7A83\u542C (eavesdropping)\uFF1A\u7B2C\u4E09\u65B9\u53EF\u4EE5\u83B7\u77E5\u901A\u4FE1\u5185\u5BB9\u3002</li><li>\u7BE1\u6539 (tampering)\uFF1A\u7B2C\u4E09\u65B9\u53EF\u4EE5\u4FEE\u6539\u901A\u4FE1\u5185\u5BB9\u3002</li><li>\u5192\u5145 (pretending)\uFF1A\u7B2C\u4E09\u65B9\u53EF\u4EE5\u5192\u5145\u4ED6\u4EBA\u8EAB\u4EFD\u53C2\u4E0E\u901A\u4FE1\u3002</li></ul><p>HTTPS \u7684\u901A\u4FE1\u5B89\u5168\u5EFA\u7ACB\u5728 SSL/TLS \u534F\u8BAE\u4E0A\uFF0C\u76EE\u7684\u662F\u4E3A\u4E86\uFF1A</p><ul><li>\u9632\u7A83\u542C\uFF1A\u6240\u6709\u4FE1\u606F\u52A0\u5BC6\u4F20\u8F93\uFF0C\u7B2C\u4E09\u65B9\u65E0\u6CD5\u7A83\u542C\u3002</li><li>\u9632\u7BE1\u6539\uFF1A\u5177\u6709\u6821\u9A8C\u673A\u5236\uFF0C\u4E00\u65E6\u88AB\u7BE1\u6539\uFF0C\u901A\u4FE1\u53CC\u65B9\u53EF\u4EE5\u7ACB\u5373\u53D1\u73B0\u3002</li><li>\u9632\u5192\u5145\uFF1A\u4F7F\u7528\u8EAB\u4EFD\u8BC1\u4E66\uFF0C\u9632\u6B62\u8EAB\u4EFD\u88AB\u5192\u5145\u3002</li></ul><p>SSL/TLS \u7684\u7248\u672C\uFF1A</p><ul><li>1994 \u5E74\uFF0CNetScape \u8BBE\u8BA1\u4E86 SSL 1.0\uFF0C\u4F46\u662F\u672A\u53D1\u5E03\u3002</li><li>1995 \u5E74\uFF0CNetScape \u53D1\u5E03\u4E86 SSL 2.0\uFF0C\u4F46\u662F\u5F88\u5FEB\u53D1\u73B0\u6709\u4E25\u91CD\u6F0F\u6D1E\u3002</li><li>1996 \u5E74\uFF0CNetScape \u53D1\u5E03\u4E86 SSL 3.0\uFF0C\u5F97\u5230\u5E7F\u6CDB\u5E94\u7528\u3002</li><li>1999 \u5E74\uFF0CISOC (Internet Society) \u63A5\u66FF NetScape \u53D1\u5E03\u4E86 SSL \u7684\u5347\u7EA7\u7248 TLS 1.0\u3002</li><li>2006 \u5E74\uFF0C\u53D1\u5E03\u4E86 TLS 1.1\u3002</li><li>2008 \u5E74\uFF0C\u53D1\u5E03\u4E86 TLS 1.2\u3002</li><li>2011 \u5E74\uFF0C\u53D1\u5E03\u4E86 TLS 1.2 \u4FEE\u8BA2\u7248\u3002</li><li>2018 \u5E74\uFF0CIETF \u53D1\u5E03\u4E86 TLS 1.3\u3002</li></ul><p>\u76EE\u524D\u7684\u4E3B\u6D41\u7248\u672C\u662F TLS 1.2/1.3\u3002</p><p>TLS 1.0 \u901A\u5E38\u79F0\u4E3A SSL 3.1\uFF0CTLS 1.1 \u79F0\u4E3A SSL 3.2\uFF0CTLS 1.2 \u79F0\u4E3A SSL 3.3\u3002</p><p>SSL/TLS \u4F7F\u7528\u516C\u94A5\u52A0\u5BC6+\u5BF9\u79F0\u52A0\u5BC6\uFF0C\u5728\u63E1\u624B\u9636\u6BB5\u83B7\u53D6\u670D\u52A1\u7AEF\u7684\u516C\u94A5\uFF0C\u5E76\u534F\u5546\u751F\u6210\u4E00\u4E2A session key\uFF0C\u516C\u94A5\u7528\u4E8E\u52A0\u5BC6 pre-master key \u7684\u4F20\u8F93\uFF0C\u540E\u7EED\u7684\u6570\u636E\u4F20\u8F93\u4F7F\u7528 HTTP \u534F\u8BAE\u548C session key \u7684\u5BF9\u79F0\u52A0\u5BC6\u3002</p><p><img src="https://s2.loli.net/2022/07/20/kHjxvKphJ375AwG.jpg" alt="\u56FE\u7247"></p><p>SSL/TLS \u7684\u5177\u4F53\u6B65\u9AA4\u5982\u4E0B\uFF1A</p><ol><li>\u5BA2\u6237\u7AEF\u53D1\u8D77\u8BF7\u6C42</li></ol><p>\u5BA2\u6237\u7AEF\u53D1\u51FA\u52A0\u5BC6\u901A\u4FE1\u7684\u8BF7\u6C42 (ClientHello)\uFF0C\u5305\u62EC\u4EE5\u4E0B\u5185\u5BB9\uFF1A</p><ul><li>\u652F\u6301\u7684\u534F\u8BAE\u7248\u672C\uFF0C\u6BD4\u5982 TLS 1.0</li><li>\u652F\u6301\u7684\u52A0\u5BC6\u7B97\u6CD5\uFF0C\u6BD4\u5982 RSA</li><li>\u5BA2\u6237\u7AEF\u751F\u6210\u7684\u968F\u673A\u6570\uFF0C\u540E\u7EED\u7528\u4E8E\u751F\u6210 session key</li><li>\u652F\u6301\u7684\u538B\u7F29\u7B97\u6CD5</li></ul><p>ClientHello \u4E2D\u4E0D\u5305\u542B\u57DF\u540D\uFF0C\u5982\u679C\u670D\u52A1\u5668\u4E0A\u90E8\u7F72\u4E86\u591A\u4E2A\u7F51\u7AD9\u548C\u8BC1\u4E66\uFF0C\u670D\u52A1\u7AEF\u4F1A\u4E0D\u77E5\u9053\u8BE5\u53D1\u56DE\u54EA\u4E2A\u7F51\u7AD9\u7684\u8BC1\u4E66\u30022006 \u5E74\uFF0CTLS \u534F\u8BAE\u52A0\u5165\u4E86 Server Name Indication (SNI)\uFF0C\u5141\u8BB8\u5BA2\u6237\u7AEF\u63D0\u4F9B\u8BF7\u6C42\u7684\u57DF\u540D\u3002</p><ol start="2"><li>\u670D\u52A1\u7AEF\u54CD\u5E94\u8BF7\u6C42</li></ol><p>\u670D\u52A1\u7AEF\u6536\u5230 ClientHello \u540E\uFF0C\u53D1\u9001\u54CD\u5E94 ServerHello\uFF0C\u5305\u62EC\u4EE5\u4E0B\u5185\u5BB9\uFF1A</p><ul><li>\u786E\u8BA4\u4F7F\u7528\u7684\u534F\u8BAE\u7248\u672C\uFF0C\u5982\u679C\u6B64\u5904\u7684\u7248\u672C\u4E0E ClientHello \u4E0D\u540C\uFF0C\u5219\u670D\u52A1\u7AEF\u5173\u95ED\u901A\u4FE1</li><li>\u786E\u8BA4\u4F7F\u7528\u7684\u52A0\u5BC6\u7B97\u6CD5</li><li>\u670D\u52A1\u7AEF\u751F\u6210\u7684\u968F\u673A\u6570\uFF0C\u540E\u7EED\u7528\u4E8E\u751F\u6210 session key</li><li>\u670D\u52A1\u7AEF\u8BC1\u4E66 (\u5305\u542B\u516C\u94A5)</li></ul><p>\u90E8\u5206\u670D\u52A1\u5668\u4F1A\u8981\u6C42\u5BA2\u6237\u7AEF\u63D0\u4F9B\u5BA2\u6237\u7AEF\u8BC1\u4E66\u3002</p><ol start="3"><li>\u5BA2\u6237\u7AEF\u6700\u540E\u56DE\u5E94</li></ol><p>\u5BA2\u6237\u7AEF\u9996\u5148\u9A8C\u8BC1\u670D\u52A1\u7AEF\u8BC1\u4E66\uFF0C\u786E\u4FDD\uFF1A</p><ul><li>\u8BC1\u4E66\u7531\u6743\u5A01\u673A\u6784\u9881\u5E03</li><li>\u8BC1\u4E66\u57DF\u540D\u4E0E\u8BF7\u6C42\u57DF\u540D\u4E00\u81F4</li><li>\u8BC1\u4E66\u672A\u8FC7\u671F</li></ul><p>\u5982\u679C\u8BC1\u4E66\u6709\u95EE\u9898\u4F1A\u5411\u7528\u6237\u53D1\u51FA\u8B66\u544A\uFF0C\u5426\u5219\u53D6\u51FA\u8BC1\u4E66\u4E2D\u7684\u516C\u94A5\uFF0C\u53D1\u51FA\u4EE5\u4E0B\u56DE\u5E94\uFF1A</p><ul><li>\u7B2C\u4E09\u4E2A\u968F\u673A\u6570 (ClientKeyExchange)\uFF0C\u4E5F\u79F0\u4E3A pre-master key\uFF0C<strong>\u7528\u516C\u94A5\u52A0\u5BC6</strong></li><li>\u7F16\u7801\u6539\u53D8\u901A\u77E5 (ChangeCipherSpec)\uFF0C\u8868\u793A\u540E\u7EED\u4FE1\u606F\u5C06\u7528\u53CC\u65B9\u534F\u5546\u7684 session key \u52A0\u5BC6\u4F20\u8F93</li><li>\u5BA2\u6237\u7AEF\u63E1\u624B\u7ED3\u675F\u901A\u77E5 (Finished)\uFF0C\u5305\u542B\u524D\u9762\u6240\u6709\u5185\u5BB9\u7684 hash \u503C\uFF0C\u4F9B\u670D\u52A1\u7AEF\u6821\u9A8C</li></ul><p>\u5BA2\u6237\u7AEF\u4F7F\u7528\u4E09\u4E2A\u968F\u673A\u6570\u548C\u4E8B\u524D\u5546\u5B9A\u7684\u52A0\u5BC6\u7B97\u6CD5\u8BA1\u7B97\u5F97\u5230 session key\u3002</p><ol start="4"><li>\u670D\u52A1\u7AEF\u6700\u540E\u56DE\u5E94</li></ol><p>\u670D\u52A1\u7AEF\u6536\u5230 pre-master key \u540E\uFF0C\u53D1\u51FA\u4EE5\u4E0B\u56DE\u5E94\uFF1A</p><ul><li>\u7F16\u7801\u6539\u53D8\u901A\u77E5 (ChangeCipherSpec)\uFF0C\u8868\u793A\u540E\u7EED\u4FE1\u606F\u5C06\u7528\u53CC\u65B9\u534F\u5546\u7684 session key \u52A0\u5BC6\u4F20\u8F93</li><li>\u670D\u52A1\u7AEF\u63E1\u624B\u7ED3\u675F\u901A\u77E5 (Finished)\uFF0C\u5305\u542B\u524D\u9762\u6240\u6709\u5185\u5BB9\u7684 hash \u503C\uFF0C\u4F9B\u5BA2\u6237\u7AEF\u6821\u9A8C</li></ul><p>\u670D\u52A1\u7AEF\u4F7F\u7528\u4E0E\u5BA2\u6237\u7AEF\u76F8\u540C\u7684\u4E09\u4E2A\u968F\u673A\u6570\u548C\u52A0\u5BC6\u7B97\u6CD5\u5F97\u5230\u76F8\u540C\u7684 session key\u3002</p>',30),S=[s];function a(n,o,r,T,h,c){return e(),i("div",null,S)}var d=l(p,[["render",a]]);export{L as __pageData,d as default};
