import{_ as e,c as a,o as t,a as c}from"./app.e29b80a0.js";const A=JSON.parse('{"title":"AJAX","description":"","frontmatter":{},"headers":[],"relativePath":"javascript/ajax.md"}'),o={name:"javascript/ajax.md"},r=c('<h1 id="ajax" tabindex="-1">AJAX <a class="header-anchor" href="#ajax" aria-hidden="true">#</a></h1><p><strong>AJAX</strong> (Asynchronous JavaScript And XML\uFF0C\u5F02\u6B65 JS \u548C XML)\uFF0C\u6307\u7684\u662F JavaScript \u53D1\u51FA\u7684\u5F02\u6B65\u7F51\u7EDC\u8BF7\u6C42\uFF0C\u53EF\u4EE5\u5B9E\u73B0\u9875\u9762\u7684\u5C40\u90E8\u66F4\u65B0\uFF0C\u4F7F\u7528\u6237\u4E0E\u9875\u9762\u7684\u4EA4\u4E92\u66F4\u987A\u7545\u3002</p><p>\u6700\u65E9\u5B9E\u73B0 AJAX \u6280\u672F\u7684\u662F <code>XMLHttpRequest</code> (\u7B80\u79F0 XHR)\uFF0C\u662F\u4E00\u4E2A\u6D4F\u89C8\u5668\u7684\u5185\u5EFA\u5BF9\u8C61\uFF0C\u5C3D\u7BA1\u540D\u5B57\u4E2D\u6709 <em>XML</em>\uFF0C\u4F46\u53EF\u4EE5\u64CD\u4F5C\u7684\u6570\u636E\u4E0D\u4EC5\u9650\u4E8E XML\uFF0C\u76EE\u524D\u5F00\u53D1\u8005\u66F4\u591A\u5730\u4F7F\u7528 <em>JSON</em> \u4F5C\u4E3A\u6570\u636E\u4EA4\u4E92\u7684\u683C\u5F0F\u3002</p><p>\u73B0\u4EE3 JavaScript \u63D0\u4F9B\u4E86 <code>fetch</code> API\uFF0C\u662F\u4E00\u79CD\u66F4\u7B80\u6D01\u901A\u7528\u7684 JS \u5F02\u6B65\u7F51\u7EDC\u8BF7\u6C42\u65B9\u5F0F\uFF0C\u7F3A\u70B9\u662F\u4E0D\u652F\u6301\u65E7\u7248\u6D4F\u89C8\u5668\u3001\u4E0D\u63D0\u4F9B XHR \u7684\u6240\u6709\u529F\u80FD\u3002\u5C3D\u7BA1\u5F00\u53D1\u8005\u5E94\u8BE5\u591A\u4F7F\u7528\u73B0\u4EE3\u7684 fetch API\uFF0C\u4F46\u662F\u76EE\u524D fetch \u8FD8\u65E0\u6CD5\u5B8C\u5168\u4EE3\u66FF XHR\uFF0C\u4ECD\u5B58\u5728\u4E00\u4E9B\u95EE\u9898\u548C\u5C40\u9650\u6027\uFF0C\u6BD4\u5982\u4E0D\u652F\u6301\u8FDB\u5EA6\u4E8B\u4EF6\u3001\u8BF7\u6C42\u8D85\u65F6\u548C\u4E2D\u6B62\u9700\u8981\u989D\u5916\u7684\u652F\u6301\u7B49\u3002</p><p>\u76EE\u524D fetch \u8FD8\u4E0D\u662F XHR \u7684\u5B8C\u7F8E\u66FF\u4EE3\u54C1\uFF0C\u5728\u90E8\u5206\u60C5\u51B5\u4E0B\u4ECD\u5FC5\u987B\u4F7F\u7528 XHR\uFF0C\u800C\u4E14 XHR \u4E5F\u4E0D\u6BD4 fetch \u5DEE\u592A\u591A\uFF0C\u5DF2\u6709\u7684\u4F7F\u7528 XHR \u7684\u5E94\u7528\u6570\u91CF\u4E5F\u5F88\u591A\uFF0C\u6BD4\u5982 Vue \u6280\u672F\u6808\u63A8\u8350\u7684\u7F51\u7EDC\u8BF7\u6C42\u5E93 <code>Axios</code> \u5728\u6D4F\u89C8\u5668\u7AEF\u5C31\u662F\u57FA\u4E8E XHR \u7684\u3002\u5BF9\u4E8E\u5F00\u53D1\u8005\u6765\u8BF4\uFF0C<code>XMLHttpRequest</code> \u548C <code>fetch</code> \u8FD9\u4E24\u79CD <strong>AJAX</strong> \u6280\u672F\u90FD\u5E94\u8BE5\u638C\u63E1\uFF0C\u6839\u636E\u5B9E\u9645\u60C5\u51B5\u786E\u5B9A\u4F7F\u7528\u54EA\u4E00\u79CD\u3002</p><p><img src="https://p1-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ce62e0aaafce48a4aa56cfbe75a60c48~tplv-k3u1fbpfcp-watermark.image?" alt="\u622A\u5C4F2022-07-30 16.05.16.png"></p>',6),p=[r];function s(n,d,i,_,f,h){return t(),a("div",null,p)}var m=e(o,[["render",s]]);export{A as __pageData,m as default};
